let semua_gadget = document.querySelectorAll(".widget:not(.Blog)");
if (semua_gadget) {
  semua_gadget.forEach((x) => {
    let semua_gambar = x.querySelectorAll("img");
    if (semua_gambar) {
      semua_gambar.forEach((x) => {
        if (!x.hasAttribute("alt")) {
          x.setAttribute("alt", "Gambar gadget");
        }
        if (!x.hasAttribute("title")) {
          x.setAttribute("title", "Gambar gadget");
        }
        if (!x.hasAttribute("width")) {
          x.setAttribute("width", "250");
        }
        if (!x.hasAttribute("height")) {
          x.setAttribute("height", "250");
        }
      });
    }
  });
}
