function coba() {
  let semua_link = document.querySelectorAll("a");
  if (semua_link) {
    semua_link.forEach((x) => {
      if (x.getAttribute("href") == "http://www.elisa-blog.com") {
        x.setAttribute("href", "https://www.elisa-blog.com");
      }
    });
  }
}
coba();
setTimeout(coba, 5000);
setTimeout(coba, 10000);
setTimeout(coba, 15000);
setTimeout(coba, 20000);
